CREATE TABLE adm_profissao
			(
			   cd_profissao integer NOT NULL, 
			   nm_profissao varchar(50), 
			   CONSTRAINT adm_profissao_codigo_pkey PRIMARY KEY (cd_profissao)
			) 
			WITH (
			  OIDS = FALSE
			);
			ALTER TABLE adm_profissao
			  OWNER TO postgres;

UPDATE pcb_bico_encerrante A 
		     SET cd_combustivel = (SELECT cd_produto_servico FROM pcb_tanque B WHERE A.cd_tanque = B.cd_tanque)
		     WHERE cd_combustivel IS NULL;

UPDATE pcb_medicao_fisica A 
		     SET cd_combustivel = (SELECT cd_produto_servico FROM pcb_tanque B WHERE A.cd_tanque = B.cd_tanque)
		     WHERE cd_combustivel IS NULL;

