       st_formacao          SMALLINT,
       nr_ano_inicio        VARCHAR(4),
       nr_ano_termino       VARCHAR(4),
       tp_nivel             SMALLINT,
       nm_curso             VARCHAR(255),
       qt_carga_horaria     SMALLINT,
       nm_titulo_trabalho   VARCHAR(255),
       nm_orientador        VARCHAR(255),
       nr_mes_obtencao_titulo SMALLINT,
       nr_ano_obtencao_titulo VARCHAR(4),
       tp_doutorado         SMALLINT,
       tp_formacao_complementar SMALLINT,
       tp_mestrado          INTEGER,
       tp_especializacao    INTEGER
	);

