CREATE OR REPLACE FUNCTION insertregistro() RETURNS trigger AS $insertRegistro$
                DECLARE
                    _CD_LOTE INTEGER := (SELECT coalesce(MAX(cd_lote_registro), 0)+1 FROM SINC_LOTE_REGISTRO);

                    _PK_NAMES CONSTANT VARCHAR[] := ARRAY(SELECT a.attname AS pk_name
                                                            FROM pg_class c, pg_attribute a, pg_index i                                           
                                                            WHERE c.oid = i.indrelid                                           
                                                            AND a.attrelid = i.indexrelid
                                                                 AND a.attisdropped = FALSE
                                                                 AND a.attnum > 0
                                                            AND i.indisprimary = TRUE
                                                            AND c.oid = TG_RELID);



                    ri RECORD;
                   	rj RECORD;

                    _VALOR_CHAVE INTEGER;

                    _NM_CHAVES_PRIMARIAS varchar := '{';

                    CD_TABELA INTEGER;

                    TP_OPERACAO INTEGER;

                    _NM_CAMPO_ALTERADO varchar := '{'; 

                    CAMPO_NOVO TEXT;  
    				CAMPO_ANTIGO TEXT;

    				j INTEGER := 0;

                BEGIN
                    FOR i IN 1..array_length(_PK_NAMES, 1)
                    LOOP
                        FOR ri IN
                            SELECT column_name
                            FROM information_schema.columns
                            WHERE
                                table_schema = quote_ident(TG_TABLE_SCHEMA)
                            AND table_name = quote_ident(TG_TABLE_NAME)
                            AND data_type = 'integer'
                            ORDER BY ordinal_position
                        LOOP
                        	IF TG_OP <> 'INSERT' THEN
                            	EXECUTE 'SELECT ($1).' || ri.column_name || '::text' INTO _VALOR_CHAVE USING OLD;
                            ELSE
                            	EXECUTE 'SELECT ($1).' || ri.column_name || '::text' INTO _VALOR_CHAVE USING NEW;
                            END IF;
                            IF ri.column_name = _PK_NAMES[i] THEN
                                EXIT;
                            END IF;
                        END LOOP;

                        IF i=1 THEN
                            _NM_CHAVES_PRIMARIAS := _NM_CHAVES_PRIMARIAS || '"' || upper(_PK_NAMES[i]) || '"' || ':' || _VALOR_CHAVE;
                        ELSE
                            _NM_CHAVES_PRIMARIAS := _NM_CHAVES_PRIMARIAS || ',' || '"' || upper(_PK_NAMES[i]) || '"' || ':' || _VALOR_CHAVE;
                        END IF;
                    END LOOP;

                    _NM_CHAVES_PRIMARIAS := _NM_CHAVES_PRIMARIAS || '}';

                    EXECUTE format('SELECT cd_tabela FROM sinc_tabela WHERE nm_tabela = $1') INTO CD_TABELA USING TG_TABLE_NAME;

                    IF TG_OP = 'INSERT' THEN
                        TP_OPERACAO = 0;
                        _NM_CAMPO_ALTERADO := '{}';
                    ELSIF TG_OP = 'UPDATE' THEN
                        TP_OPERACAO = 1;
                        FOR rj IN
                            SELECT column_name, data_type
                            FROM information_schema.columns
                            WHERE
                                table_schema = quote_ident(TG_TABLE_SCHEMA)
                            AND table_name = quote_ident(TG_TABLE_NAME)
                            ORDER BY ordinal_position
                        LOOP
                        	Execute 'SELECT ($1).' || rj.column_name || '::text' into CAMPO_ANTIGO using OLD;  
						    Execute 'SELECT ($1).' || rj.column_name || '::text' into CAMPO_NOVO using NEW;
						    IF ((rj.data_type = 'timestamp') OR (rj.data_type = 'timestamp without time zone') OR (rj.data_type = 'timestamp with time zone')) THEN
                        		CAMPO_ANTIGO := substring(CAMPO_ANTIGO from 1 for 10);
                        		CAMPO_NOVO   := substring(CAMPO_NOVO from 1 for 10);	
                        	END IF;  
                        	IF CAMPO_NOVO <> CAMPO_ANTIGO OR ((CAMPO_ANTIGO IS NULL OR CAMPO_NOVO IS NULL) AND ((CAMPO_ANTIGO IS NOT NULL) OR (CAMPO_NOVO IS NOT NULL))) THEN
					  			IF j=0 THEN
		                            _NM_CAMPO_ALTERADO := _NM_CAMPO_ALTERADO || '"' || upper(rj.column_name) || '"' || ':' || '"' || (CASE WHEN (CAMPO_ANTIGO IS NOT NULL) THEN CAMPO_ANTIGO ELSE '' END) || '|' || (CASE WHEN (CAMPO_NOVO IS NOT NULL) THEN CAMPO_NOVO ELSE '' END) || '"';
		                        ELSE
		                            _NM_CAMPO_ALTERADO := _NM_CAMPO_ALTERADO || ',' || '"' || upper(rj.column_name) || '"' || ':' || '"' || (CASE WHEN (CAMPO_ANTIGO IS NOT NULL) THEN CAMPO_ANTIGO ELSE '' END) || '|' || (CASE WHEN (CAMPO_NOVO IS NOT NULL) THEN CAMPO_NOVO ELSE '' END) || '"';
		                        END IF;
		                        j := j + 1;
		                    END IF;
				        END LOOP;

				        _NM_CAMPO_ALTERADO := _NM_CAMPO_ALTERADO || '}';

				        _NM_CAMPO_ALTERADO := regexp_replace(_NM_CAMPO_ALTERADO, E'[\\n\\r\\t]+', ' ', 'g');


                    ELSE          
                        TP_OPERACAO = 2;
                        _NM_CAMPO_ALTERADO := '{}';
                    END IF;   

                    INSERT INTO SINC_LOTE_REGISTRO (CD_LOTE_REGISTRO, CD_TABELA, NM_VALOR_CHAVES, TP_ATUALIZACAO, DT_ATUALIZACAO, NM_CAMPO_ALTERADO) VALUES (_CD_LOTE, CD_TABELA, _NM_CHAVES_PRIMARIAS, TP_OPERACAO, now(), _NM_CAMPO_ALTERADO);

                    RETURN NEW;
                END; 

                $insertRegistro$ LANGUAGE plpgsql;

